															}.AttachTo(context.Document.body);

															FuturePoster.Continue(
																(IHTMLImage Poster) =>
																{
																	("FuturePoster.Continue: " + key).AttachAsDiv(context);

																	Poster.style.cursor = "pointer";
																	Poster.onclick +=
